import pandas as pd

df = pd.read_csv('checking_output/predictions_1066_1618_robbert-v2-dutch-base_23052024.csv', sep = '\t')

predictions_check = df['prediction'].tolist()
gold = df['gold'].tolist()

print(predictions_check)

tn=0
tp=0
fn=0
fp=0
zipped = zip(predictions_check, gold)
for pred, gold in zipped:
    if pred == gold and pred == 'O':
        tn+=1
    if pred == gold and pred == 'I-event':
        tp+=1
    if pred != gold and gold == 'I-event':
        fn+=1
    if pred != gold and gold == "O":
        fp+=1

try:
    precision = tp / (tp+fp)
    recall = tp / (tp+fn)
    f1 = 2 * (precision * recall) / (precision + recall)
except ZeroDivisionError:
    precision = 0
    recall = 0
    f1 = 0

print(precision, recall, f1)